Output parameters: {"is_holiway": {"type": "string", "description": "Availability of a suitable vehicle. Enumerated type, [no, yes]"}, "holiway_message": {"type": "object", "description": "Trip Information Sheet, being empty if no suitable vehicle has been found", "properties": {"travel_time": {"type": "string", "description": "The specific time of departure, year + month + day + hour + minute, for example, \"10:30 on February 6, 2037\""}, "travel_position": {"type": "string", "description": "The departure station location, for example, \"Chongqing North Railway Station\""}, "travel_price": {"type": "string", "description": "Ticket price, numeric value + yuan, for example, \"355 yuan\""}, "travel_link": {"type": "string", "description": "Ticket purchase link, for example, \"https://trains.ctrip.com/webapp/train/list?ticketType=0&dStation=%E4%B8%8A%E6%B5%B7&aStation=%E5%8C%97%E4%BA%AC&dDate=2024-04-09&rDate=&trainsType=&hubCityName=&highSpeedOnly=0\""}}}}

